import React from "react";
import { Link } from "react-router-dom"
import data from "../data"

export default function List(){
    return(
        <div className="mt-5">
            {data.map(itm => {
                // Remplacer chaque element par un tiret
                //  const title = post?.title.split(" ").join("-");
                 const title = itm.title.split(" ").join("-");
                //  Afficher seulement 100 caractere et du contenu static ...
                return(
                    <div key={itm.id}>
                         {/* <h3><Link to="/post/title">{itm.title}</Link></h3> */}
                         {/* <h3><Link to={`/post/${title}`} state={{id : post._id}}>{post.title}</Link></h3> */}
                         <h3><Link to={`/post/${title}`}>{itm.title}</Link></h3>
                        {/* <h3>{itm.title}</h3> */}
                        <p>{itm.content}</p>
                    </div>
                )
            })}
        </div> 
    )
}
